import React, { Component } from 'react';
import { Container } from "reactstrap"
import axios from "axios";
import Loading from "./Components/Loading"

import logo from './logo.svg';
import Message from "./Message";
import Button from "./Button";
import Header from "./Components/Header";
import NewGame from "./Components/NewGame";
import './App.css';
import Axios from 'axios';
import PlayGame from './Components/PlayGame';

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      showImg: false,
      message: "Hello world",
      num: 0,
      game: null,
      loading: true
    }
  }

  componentWillMount(){
    console.log("Will mount")
  }

  componentDidMount(){
    console.log("Did mount")
    if(window.location.pathname){
      const pathParams = window.location.pathname.slice(1).split("/")
      const questionId = pathParams[1]
      if(pathParams[1] && pathParams[0] === "game"){
        axios({
          url: `http://10.10.3.188:6969/api/game/${questionId}`,
          method: "GET",
        }).then(response => {
          console.log(response.data)
          if(response.data.success){
            setTimeout(() =>{
              this.setState({game: response.data.game, loading: false});
            }, 1000);
          }
        }).catch(error => {
          console.log(error);
        })
      } else{
        this.setState({loading: false, game: null})
      }
    }
  }

  render() {
    const {game, loading} = this.state
    console.log("Render")
    return (
      <Container className="App container">
        <Header/>
        {loading ? <div className="text-center">
          <Loading />
        </div> : (game ? <PlayGame data /> : <NewGame toggleLoading={() => {this.setState({loading})}}/>)}
        {/* <header className="App-header">
          {this.state.showImg ? <img src={logo} className="App-logo" alt="logo" /> : "Hidden"}
          <div>
            <Message message={this.state.message}/>
          </div>
          <div>
            <p>Click: {this.state.num}</p>
            <Button handleClick={() => {this.setState({num: this.state.num + 1})}}></Button>
          </div>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header> */}
      </Container>
    );
  }
}

export default App;
